commits.content:
import Image from "next/image";
import sampleImg from "@/assets/home/image/custom/featuredNews.svg";

export default function FeaturedNews() {
  const news = [
    {
      title: "Can I Really Retire at 55 in Vietnam?",
      category: "Financial",
      desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc maximus suscipit felis eu cursus. Ut cursus mauris id enim suscipit.",
      img: sampleImg,
    },
    {
      title: "Business growth strategy",
      category: "Business",
      desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer nec odio. Praesent libero. Sed cursus ante dapibus diam.",
      img: sampleImg,
    },
    {
      title: "Smart investment tips",
      category: "Investment",
      desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Ut cursus mauris id enim suscipit. Vivamus sagittis lacus vel augue laoreet.",
      img: sampleImg,
    },
    {
      title: "Market analysis 2025",
      category: "Market",
      desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis aute irure dolor in reprehenderit in voluptate velit esse.",
      img: sampleImg,
    },
  ];

  return (
    <section className="py-16 bg-gray-50">
      <div className="max-w-6xl mx-auto px-4">
        {/* Heading */}
        <div className="text-center max-w-2xl mx-auto mb-12">
          <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
            Featured News
          </h2>
          <p className="text-gray-600 text-sm md:text-base">
            Pallamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute
            irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
          </p>
        </div>

        {/* News list */}
        <div className="space-y-12 md:space-y-16">
          {news.map((item, index) => (
            <div
              key={index}
              className={`flex flex-col md:flex-row items-center md:items-start gap-6 md:gap-12 ${
                index % 2 === 1 ? "md:flex-row-reverse" : ""
              }`}
            >
              {/* Image */}
              <div className="w-full md:w-1/2 overflow-hidden rounded-lg shadow-md">
                <Image
                  src={item.img}
                  alt={item.title}
                  className="w-full h-auto object-cover rounded-lg transform hover:scale-105 transition duration-500"
                />
              </div>

              {/* Content */}
              <div className="w-full md:w-1/2 space-y-3 text-center md:text-left">
                <h3 className="text-xl md:text-2xl font-semibold text-blue-600">
                  {item.title}
                </h3>
                <p className="text-sm font-medium text-gray-500 uppercase tracking-wide">
                  {item.category}
                </p>
                <p className="text-gray-600 text-sm md:text-base">{item.desc}</p>
                <a
                  href="#"
                  className="inline-block mt-2 text-blue-600 font-medium hover:underline"
                >
                  Read More »
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
