import { FaQuoteLeft, FaStar } from "react-icons/fa";

const testimonials = [
  {
    content:
      "This services have helped us successfully digitize. Professional team with enthusiastic support.",
    name: "John Smith",
    title: "CEO of ABC Company",
    avatar: "https://i.pravatar.cc/100?img=1",
  },
  {
    content:
      "The application developed by Dudi has helped us increase revenue by 30%. Friendly interface, easy to use with stable performance.",
    name: "Jane Doe",
    title: "Director of XYZ Company",
    avatar: "https://i.pravatar.cc/100?img=2",
  },
  {
    content:
      "The management system built by Dudi has saved 30% of operation time. Very satisfied with the service quality.",
    name: "Robert Johnson",
    title: "CTO of DEF Company",
    avatar: "https://i.pravatar.cc/100?img=3",
  },
];

export default function Testimonials() {
  return (
    <section className="py-16 bg-gray-50">
      <div className="container mx-auto px-4 sm:px-6 lg:px-12 text-center">
        {/* Heading */}
        <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold text-gray-800">
        What Sets Us Apart
        </h2>
        <p className="text-gray-500 mt-2 text-sm sm:text-base">
          Feedback from clients who have used our services
        </p>

        {/* Testimonials grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mt-10">
          {testimonials.map((item, i) => (
            <div
              key={i}
              className="bg-white rounded-xl shadow-md p-6 sm:p-8 text-left hover:shadow-lg transition"
            >
              <FaQuoteLeft className="text-green-600 text-2xl sm:text-3xl mb-3" />
              <p className="text-gray-700 mb-4 text-sm sm:text-base">
                {item.content}
              </p>
              <div className="flex items-center gap-3">
                <img
                  src={item.avatar}
                  alt={item.name}
                  className="w-10 h-10 sm:w-12 sm:h-12 rounded-full"
                />
                <div>
                  <p className="font-semibold text-sm sm:text-base">{item.name}</p>
                  <p className="text-xs sm:text-sm text-gray-500">{item.title}</p>
                </div>
              </div>
              <div className="flex mt-3 text-yellow-400 text-sm sm:text-base">
                {[...Array(5)].map((_, idx) => (
                  <FaStar key={idx} />
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
