"use client";

import BannerImage from "@/assets/about/banner aboutus.png";
import Image from "next/image";
import { useLanguage } from "@/contexts/LanguageContext";

export default function HeroSection() {
  const { t } = useLanguage();
  
  return (
    <section className="relative w-full">
      {/* Container to maintain aspect ratio, responsive on all screens */}
      <div className="relative w-full aspect-[1200/380] max-h-[380px]">
        <Image
          src={BannerImage}
          alt="About Us Banner"
          fill
          priority
          className="object-cover" // fill image without distortion
        />

        {/* Text Overlay with better contrast */}
        <div className="absolute inset-0 flex flex-col items-center justify-center text-center px-4 bg-black/20">
          <h1 className="text-xl sm:text-2xl md:text-4xl lg:text-5xl font-bold text-white drop-shadow-lg">
            {t("introduce.hero.title")}
          </h1>
          <p className="mt-2 text-xs sm:text-sm md:text-lg text-white drop-shadow-md max-w-2xl">
            {t("introduce.hero.subtitle")}
          </p>
        </div>
      </div>
    </section>
  );
}
