import { notFound } from "next/navigation";
import Banner from "./components/Banner";
import Employee from "./components/Employee";
import DifferentEmployee from "./components/DifferentEmployee";
import MTP from "../../../../../../public/mtp.svg";

const employees = [
  {
    id: "nguyen-van-a",
    name: "Nguyen Van A",
    role: "CEO",
    img: MTP,
    bio: `Larry Truong – Money Coach & Managing Director | Money Coach & Giám Đốc Điều Hành
Larry Truong is a seasoned professional in personal finance, with over 30 years of experience across both global and local markets—including two decades deeply engaged in Vietnam’s evolving financial services landscape. He leads MoneyWise Vietnam with a focus on delivering practical, high-impact personal finance solutions tailored to the needs of Vietnamese individuals and enterprises. The professional development programs he and his team have implemented have empowered over 50,000 financial advisors, sales managers, and 1,000 employees through transformative learning systems—setting new benchmarks for professionalism across the industry.
  },
  {
    id: "tran-thi-b",
    name: "Tran Thi B",
    role: "CTO",
    img: MTP,
    bio: "CTO introduction content here...",
  },
  {
    id: "le-van-c",
    name: "Le Van C",
    role: "Designer",
    img: MTP,
    bio: "Designer introduction content here...",
  },
  {
    id: "pham-thi-d",
    name: "Pham Thi D",
    role: "Developer",
    img: MTP,
    bio: "Developer introduction content here...",
  },
  {
    id: "hoang-van-e",
    name: "Hoang Van E",
    role: "Marketing",
    img: MTP,
    bio: "Marketing introduction content here...",
  },
];

export default function EmployeeDetail({ params }: { params: { id: string } }) {
  const emp = employees.find((e) => e.id === params.id);

  if (!emp) return notFound();

  return (
    <div className="w-full h-full">
      <Banner />
      <Employee emp={emp} />
      <DifferentEmployee employees={employees} />
    </div>
  );
}
