// employee/components/Members.tsx
import Image from "next/image";
import Link from "next/link";
import MTP from "../../../../../../public/mtp.svg";

export default function Members() {
  const employees = [
    { id: "nguyen-van-a", name: "Nguyen Van A", role: "CEO", img: MTP },
    { id: "tran-thi-b", name: "Tran Thi B", role: "CTO", img: MTP },
    { id: "le-van-c", name: "Le Van C", role: "Designer", img: MTP },
    { id: "pham-thi-d", name: "Pham Thi D", role: "Developer", img: MTP },
    { id: "hoang-van-e", name: "Hoang Van E", role: "Marketing", img: MTP },
  ];

  return (
    <div className="flex flex-col gap-12 items-center mt-24 px-4">
      {/* Top row - 3 members */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-12 w-full max-w-6xl">
        {employees.slice(0, 3).map((emp) => (
          <Link
            key={emp.id}
            href={`/introduce/employee/${emp.id}`}
            className="bg-white shadow-md rounded-lg flex flex-col text-center hover:shadow-lg transition overflow-hidden"
          >
            <Image
              src={emp.img}
              alt={emp.name}
              className="w-full h-128 object-cover rounded-t-lg"
            />
            <div className="p-6">
              <h3 className="font-semibold text-lg">{emp.name}</h3>
              <p className="text-gray-600">{emp.role}</p>
            </div>
          </Link>
        ))}
      </div>

      {/* Bottom row - 2 members */}
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-12 w-full max-w-4xl lg:max-w-3xl">
        {employees.slice(3).map((emp) => (
          <Link
            key={emp.id}
            href={`/introduce/employee/${emp.id}`}
            className="bg-white shadow-md rounded-lg flex flex-col text-center hover:shadow-lg transition overflow-hidden"
          >
            <Image
              src={emp.img}
              alt={emp.name}
              className="w-full h-128 object-cover rounded-t-lg"
            />
            <div className="p-6">
              <h3 className="font-semibold text-lg">{emp.name}</h3>
              <p className="text-gray-600">{emp.role}</p>
            </div>
          </Link>
        ))}
      </div>

      {/* Divider */}
      <div className="w-[80%] border-t border-green-800 mt-4 mx-auto"></div>
    </div>
  );
}
