"use client";
import { useEffect, useState } from "react";
import { serviceService } from "@/services/service.service";
import { Service } from "@/types/service";
import ServiceCard from "./components/ServiceCard";
import Link from "next/link";
import Contact from "@/components/Contact";
import { useLanguage } from "@/contexts/LanguageContext";

export default function ServicesPage() {
  const [services, setServices] = useState<Service[]>([]);
  const [loading, setLoading] = useState(true);
  const { t } = useLanguage(); // Sử dụng i18n để lấy text

  useEffect(() => {
    const fetchServices = async () => {
      try {
        setLoading(true);
        const data = await serviceService.getAll();
        setServices(data);
      } catch (err) {
        console.error("❌ Fetch services error:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchServices();
  }, []);

  if (loading) return <p className="text-center mt-10">{t("loading")}</p>;

  return (
    <div className="max-w-6xl mx-auto px-6 py-10">
      <h1 className="text-3xl font-bold mb-6 text-center relative">
        {t("service.title")} {/* Lấy text từ i18n */}
        <span className="absolute bottom-[-4px] left-1/2 transform -translate-x-1/2 w-20 h-1 bg-green-700"></span>
      </h1>

      {services.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {services.map((srv) => (
            <Link key={srv._id} href={`/service/${srv._id}`}>
              <ServiceCard
                title={srv.title}
                description={srv.description || ""}
                image={srv.icon || "/images/default-service.jpg"}
              />
            </Link>
          ))}
        </div>
      ) : (
        <p className="text-center text-gray-500 mt-10">
          {t("service.noServices")} {/* Lấy text từ i18n */}
        </p>
      )}

      <div className="w-[80%] border-t border-green-800 mt-6 mx-auto"></div>
      <Contact />
    </div>
  );
}
