import api from "./api";
import { Topic } from "@/types/topic";

export const topicService = {
  /** ðŸŸ© Láº¥y táº¥t cáº£ topics */
  getAll: async (): Promise<Topic[]> => {
    const res = await api.get("/topics");
    return Array.isArray(res.data?.topics) ? res.data.topics : [];
  },

  /** ðŸŸ¦ Láº¥y topic theo slug */
  getBySlug: async (slug: string): Promise<Topic> => {
    const res = await api.get(`/topics/${slug}`);
    return res.data?.topic || res.data;
  },

  /** ðŸŸ¨ Táº¡o má»›i topic */
  create: async (data: Partial<Topic>, files?: { banner?: File }): Promise<Topic> => {
    const formData = new FormData();

    if (files?.banner instanceof File) formData.append("banner", files.banner);

    if (data.slug) formData.append("slug", data.slug.trim());
    if (data.title) formData.append("title", data.title.trim());
    if (data.subtitle) formData.append("subtitle", data.subtitle.trim());

    const res = await api.post("/topics", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });
    return res.data?.topic || res.data;
  },

  /** ðŸŸ§ Cáº­p nháº­t topic */
  update: async (slug: string, data: Partial<Topic>, files?: { banner?: File }): Promise<Topic> => {
    const formData = new FormData();

    if (files?.banner instanceof File) formData.append("banner", files.banner);

    if (data.title) formData.append("title", data.title.trim());
    if (data.subtitle) formData.append("subtitle", data.subtitle.trim());

    const res = await api.put(`/topics/${slug}`, formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });
    return res.data?.topic || res.data;
  },

  /** ðŸŸ¥ XÃ³a topic */
  delete: async (slug: string): Promise<{ message: string }> => {
    const res = await api.delete(`/topics/${slug}`);
    return res.data;
  },
};
