import { StaticImageData } from "next/image";

export interface Post {
  _id?: string;
  id: number;
  title: string;
  excerpt: string;
  content?: string;
  type: "image" | "video";
  image?: StaticImageData | string;
  videoUrl?: string;
  author: string;
  topicSlug?: string;
  createdAt?: string;
  updatedAt?: string;
}

export interface Topic {
  _id?: string;
  slug: string;
  title: string;
  subtitle: string;
  banner: StaticImageData | string;
  posts: Post[];
  createdAt?: string;
  updatedAt?: string;
}
